mapper.select:
select *
from game
where game_name like #{gameName}

select * from game where user_id > 0

    and game_type=#{param1}


    and game_name like #{param2}

select * from game where user_id > 0

    and game_type=#{param3}


    and game_name like #{param4}

and game_id in ( select game_id from (
select game_id from game where user_id > 0

    and game_type=#{param3}


    and game_name like #{param4}

and game_id not in
(
select game_id from (select game_id from game where user_id > 0

        and game_type=#{param3}


        and game_name like #{param4}

    LIMIT #{param2} ) as a)
) as b ) limit #{param1}

select * from game where user_id < 0

    and game_type=#{param3}

and game_id in ( select game_id from (
select game_id from game where user_id < 0

    and game_type=#{param3}

and game_id not in
(select game_id from (select game_id from game where user_id < 0

    and game_type=#{param3}

LIMIT #{param2} ) as a)
) as b ) limit #{param1}

select * from game where user_id < 0

    and game_type=#{param3}


    order by game_store

and game_id in ( select game_id from (
select game_id from game where user_id < 0

    and game_type=#{param3}


    order by game_store

and game_id not in
(select game_id from (select game_id from game where user_id > 0

    and game_type=#{param3}


    order by game_store

LIMIT #{param2} ) as a)
) as b ) limit #{param1}
